mapper.select:
		select `id`,`timu_id`,`choose_name`,`sort`,`add_time`,`del_flag`,type,jump_flag,jump_title_id from t_zhongyi_title_choose

		  		   and id = #{id} 
		  		   and timu_id = #{timuId} 
		  		   and choose_name = #{chooseName} 
		  		   and sort = #{sort} 
		  		   and add_time = #{addTime} 
		  		   and del_flag = #{delFlag} 
		  		   and type = #{type} 
		  		   and jump_flag = #{jumpFlag} 
		  		   and jump_title_id = #{jumpTitleId} 



                order by ${sort} ${order}


                order by sort asc



			limit #{offset}, #{limit}

select count(*) from t_zhongyi_title_choose

